    //--------------------------------------------------- To Decimal Kodlari ---------------------------------------------------------------------------------------------
    
    
    public static int bcdToDecimal(String s) 
    { 
        int len = s.length(); 
        int check = 0, check0 = 0; 
        int num = 0, sum = 0; 
        int mul = 1, rev = 0; 
  

        for(int i = len - 1; i >= 0; i--)  
        { 
  

            sum += (s.charAt(i) - '0') * mul; 
            mul *= 2; 
            check++; 

            if (check == 4 || i == 0)  
            { 
                if (sum == 0 && check0 == 0)  
            { 
               num = 1; 
               check0 = 1; 
            } 
                else
            { 
  

               num = num * 10 + sum; 
            } 
             
            check = 0; 
            sum = 0; 
            mul = 1; 
       } 
    } 

        while (num > 0)  
    { 
        rev = rev * 10 + (num % 10); 
        num /= 10; 
    } 
  
        if (check0 == 1) 
            return rev - 1; 
  
        return rev; 
    } 
