        public static String GrayToDecimal(String s) {
            
            int gray = Integer.parseInt(s);
            int bin = 0;
            while (gray != 0) {
                int dec = gray - 1;
                bin ^= gray;
                bin ^= dec;
                gray &= dec;
            }
        int decimal = Integer.parseInt(Integer.toString(bin),2);
        return Integer.toString(decimal);

}
       
    
      
    
    //--------------------------------------- Decimalden diger kodlama turlerine -----------------------------------------------------------
    
    
    
       public static String toBCD(int num)
    {
        String BCD="";
        while(num!=0)
        {
        int t=num%10;
        String tBCD=Integer.toBinaryString(t);
        while(tBCD.length()<4)
            {
            tBCD="0"+tBCD;
            }
        BCD=tBCD+BCD;
        num/=10;
        }
        return BCD;
    }
       
        public static String toExcess3(int num)
    {
        String output = new String();;
        String numstring = String.valueOf(num);
        int[] arraynum = String.valueOf(num).chars().map(Character::getNumericValue).toArray();
        String[] binarycode =  new String[arraynum.length];
        
        
        for (int i = 0; i < arraynum.length; i++){
            arraynum[i] += 3;
            binarycode[i] = String.format("%4s", Integer.toBinaryString(arraynum[i])).replace(' ', '0');
            output += " " + binarycode[i]; 
        }  

        return  output;
    }
        
        
       public static String toAiken(int num)
    {
        
        int[] arraynum = String.valueOf(num).chars().map(Character::getNumericValue).toArray();
        String output = new String();
        String binarystring = new String();
        
        for (int i = 0; i < arraynum.length; i++){
                switch (arraynum[i]) { 
        case 1: 
            binarystring  = "0001"; 
            break; 
        case 2: 
            binarystring  = "0010"; 
            break; 
        case 3: 
            binarystring  = "0011"; 
            break; 
        case 4: 
            binarystring  = "0100"; 
            break; 
        case 5: 
            binarystring = "1011"; 
            break; 
        case 6: 
            binarystring  = "1100"; 
            break; 
        case 7: 
            binarystring = "1101"; 
            break; 
        case 8: 
            binarystring = "1110"; 
            break; 
        case 9: 
            binarystring  = "1111"; 
            break; 
        case 0: 
            binarystring  = "0000"; 
            break; 
        default: 
            binarystring = null; 
        } 
        output += " " + binarystring;       

        }  
        
            
        return output;
    }
      public static String toGray(int num)
    {      
        return String.valueOf(getGreyCode(num));
    }
    static String getGreyCode(int myNum, int numOfBits) {
        
        if (numOfBits == 1) {
            return String.valueOf(myNum);
        }

        if (myNum >= Math.pow(2, (numOfBits - 1))) {
            return "1" + getGreyCode((int)(Math.pow(2, (numOfBits))) - myNum - 1, numOfBits - 1);
        } else {
            return "0" + getGreyCode(myNum, numOfBits - 1);
            }
        }

    static String getGreyCode(int myNum) {

        int numOfBits = (int)(Math.log(myNum) / Math.log(2)) + 1;
        return getGreyCode(myNum, numOfBits);
    }
     
    
    
    


