    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        if(jTextField3.getText().equals("")) {
                jTextField4.setText("Dönüştürülecek deger girilmedi.");
            
        }else if (jTextField1.getText().equals("")){
            
                jTextField4.setText("Baslangic tabani girilmedi.");
                
        }else if(jTextField2.getText().equals("")){
            
            jTextField4.setText("Dönüştürülecek taban girilmedi.");
        }else{
            int from = Integer.parseInt(jTextField1.getText());
            int to = Integer.parseInt(jTextField2.getText());
            String s = jTextField3.getText();
            if(from == to){
                jTextField4.setText(s);           
            }else{
                String t = convertFromBaseToBase(s,from,to);
                jTextField4.setText(t);
            }
            
            
            
        }

    }//GEN-LAST:event_jButton2ActionPerformed

