    public String convertFromBaseToBase(String num, int base1, int base2) {
        
        int no = convFrmBaseToDeci(num, base1);
        return convFrmDecToBase(no, base2);
    }
    static public String convFrmDecToBase(int num, int base) {

        String res = "";
        int rem;
        while (num > 0) {
            rem = num % base;
            if (base == 16) {
                if (rem == 10)
                    res += 'A';
                else if (rem == 11)
                    res += 'B';
                else if (rem == 12)
                    res += 'C';
                else if (rem == 13)
                    res += 'D';
                else if (rem == 14)
                    res += 'E';
                else if (rem == 15)
                    res += 'F';
                else
                    res += rem;
            } else
                res += rem;

            num /= base;
        }
        return new StringBuffer(res).reverse().toString();
    }

     public String biretumleme(String a) {

        StringBuilder raw = new StringBuilder(a);
        for (int i = 0; i < a.length(); i++) {
            if (a.charAt(i) == '0'){
                raw.setCharAt(i, '1');                
            }else if(a.charAt(i) == '1'){
                raw.setCharAt(i, '0');  
            }
        }
        a = raw.toString();
        return a;
    }
    static int convFrmBaseToDeci(String num, int base) {

        if (base < 2 || (base > 10 && base != 16))
            return -1;

        int val = 0;
        int power = 1;

        for (int i = num.length() - 1; i >= 0; i--) {
            int digit = digitToVal(num.charAt(i));

            if (digit < 0 || digit >= base)
                return -1;


            val += digit * power;
            power = power * base;
        }

        return val;
    }

    static int digitToVal(char c) {
        if (c >= '0' && c <= '9')
            return (int) c - '0';
        else
            return (int) c - 'A' + 10;
    }
    
       static String ikiyetumleme(String a) 
    { 
        StringBuffer str = new StringBuffer(a);
        int n = str.length(); 
       
        // Traverse the string to get first '1' from 
        // the last of string 
        int i; 
        for (i = n-1 ; i >= 0 ; i--) 
            if (str.charAt(i) == '1') 
                break; 
       
        // If there exists no '1' concat 1 at the 
        // starting of string 
        if (i == -1) 
            return "1" + str; 
       
        // Continue traversal after the position of 
        // first '1' 
        for (int k = i-1 ; k >= 0; k--) 
        { 
            //Just flip the values 
            if (str.charAt(k) == '1') 
                str.replace(k, k+1, "0"); 
            else
                str.replace(k, k+1, "1"); 
        } 
       
        // return the modified string 
        return str.toString(); 
    } 
       static String tumlemearitmatigi(String a,String b){
           
           int result;
           int b1;
           int a1 = Integer.valueOf(a);
           int a2 = Integer.valueOf(b);
           
           if (a1 > a2){              
               b1 = a.length();          
           }else if(a2 > a2){
               b1 = b.length();
           }else{
               b1 = a.length();
           }
           double z = Math.pow(10,b1);
           int asd = (int)z - a2;
           asd = asd + a1;
           
           if(asd > Math.pow(10,b1)){
               
               asd =   asd - (int)Math.pow(10,b1);
           }else{
               asd = (int)Math.pow(10,b1) - asd;
               asd = -asd;
           }
           
           return Integer.toString(asd);
                  
       }
       static String ikiyetumlemearitmatigi(String a,String b){
           double asd = 0;
           int a1 = Integer.valueOf(a);
           int b1 = Integer.valueOf(b);
           String a21;
           String a2 = ikiyetumleme(b);
           String toplama = addBinary(a,a2);

           if(toplama.length()>a.length())
           {
               
               asd = Integer.valueOf(toplama) - Math.pow(10, a.length());
               int ads = (int)asd;
               a21 = Integer.toString(ads);
               
           }else {
               a21 = ikiyetumleme(toplama);
               a21 = "-"+a21;
           }
           
           for (int i = a21.length(); a.length()>a21.length();i++ )
           {
            a21 = "0"+a21;
           } 
           return a21;
       }
       
          static String addBinary(String a, String b)
    {
        String result = ""; 
        int s = 0;         
        int i = a.length() - 1, j = b.length() - 1;
        while (i >= 0 || j >= 0 || s == 1)
        {

            s += ((i >= 0)? a.charAt(i) - '0': 0);
            s += ((j >= 0)? b.charAt(j) - '0': 0);
            result = (char)(s % 2 + '0') + result;
            s /= 2;
            i--; j--;
        }
    return result;
    }
    
